im = imread('crocodileoriginal.jpg');ig = rgb2gray(im);imWidth = 320;imHeight = 240;for file_num = 1:10  filename = sprintf("in%.2d.txt", file_num);  filebin = "in.bin";  file = fopen(filename,'w');  for i = ((file_num-1)*(imHeight/10) + 1):(file_num*imHeight/10)    for j = 1:imWidth      fprintf(file, "%s\n", dec2bin(ig(i,j), 8));    endfor  endfor  fclose(file);endforbin = fopen(filebin, 'w');fwrite(bin, ig);